<script>

    import {getAPI} from "@/axios";
    import VueContentLoading from "vue-content-loading";

    export default {
        data() {
            return {
                meetings: [],
                join_meeting: {},
                backgroundLoading: 'primary',
                colorLoading: '#fff',
                loading: false,
            }
        },
        watch: {},
        computed: {


            windowWidth() {
                return this.$store.state.windowWidth
            }
        },
        methods: {
            getJoinMeetingLink(meeting_id) {
                getAPI.get('api/v1/meeting/join-meeting/', {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    params: {
                        meeting_id: meeting_id,
                    }
                }).then(response => {
                    setTimeout(() => {
                        console.log('heheheh')
                    }, 2000);
                    this.$vs.loading.close("#button-with-loading > .con-vs-loading")
                    console.log(response.data.valid)
                    this.join_meeting = response.data
                    window.open(response.data.join_link, '_blank');
                    // console.log(response.data);
                }).catch(error => {
                    // this.$vs.loading.close("#button-with-loading > .con-vs-loading")
                    console.log(error);
                    // commit('showErrorAlert', error.data)
                    // console.log(error.response.data);
                })
            },
            getMeeting() {
                getAPI.get('api/v1/meeting/meetings-list').then(response => {
                    // commit('offerPosts', response.data)
                    setTimeout(() => {
                        console.log('heheheh')
                    }, 2000);
                    console.log(response.data['results'].id)
                    this.meetings.push(...response.data['results'])
                    // console.log(response.data);
                }).catch(error => {
                    console.log(error);
                    // commit('showErrorAlert', error.data)
                    // console.log(error.response.data);
                })
            },
            openLoadingContained(meeting_id) {
                this.$vs.loading({
                    background: this.backgroundLoading,
                    color: this.colorLoading,
                    container: "#button-with-loading",
                    scale: 0.45
                });

                this.getJoinMeetingLink(meeting_id)

            },
        },
        components: {
            VueContentLoading
            // VueApexCharts,
            // VueContentLoading,
            // VclFacebook
        },
        created() {

        },
        beforeDestroy() {
        },
        mounted() {
            this.getMeeting();
        }
    }

</script>


